<?phpclass girlAction extends HomeAction {    public function index() {        $n = M('girl');        import("@.ORG.Page");        $count = $n->where($map)->count();        $p = new Page($count, 20);        $girl = $n->where($map)->order('id desc')->limit($p->firstRow . ',' . $p->listRows)->select();        $page = $p->show();        $this->assign('page', $page);        $this->assign('girl', $girl);        $this->display();    }}?>